glsl3.vert:
precision highp float;
uniform CCGlobal {
  vec4 cc_time;

  vec4 cc_screenSize;

  vec4 cc_screenScale;

  vec4 cc_nativeSize;

  mat4 cc_matView;
  mat4 cc_matViewInv;
  mat4 cc_matProj;
  mat4 cc_matProjInv;
  mat4 cc_matViewProj;
  mat4 cc_matViewProjInv;
  vec4 cc_cameraPos;

  vec4 cc_exposure;

  vec4 cc_mainLitDir;

  vec4 cc_mainLitColor;

  vec4 cc_ambientSky;
  vec4 cc_ambientGround;
};
uniform CCLocal {
  mat4 cc_matWorld;
  mat4 cc_matWorldIT;
};

in vec3 a_position;
in vec4 a_color;
out vec4 v_color;

#if USE_TEXTURE
in vec2 a_uv0;
out vec2 v_uv0;
#endif

void main () {
  vec4 pos = vec4(a_position, 1);

  #if CC_USE_MODEL
  pos = cc_matViewProj * cc_matWorld * pos;
  #else
  pos = cc_matViewProj * pos;
  #endif

  #if USE_TEXTURE
  v_uv0 = a_uv0;
  #endif

  v_color = a_color;

  gl_Position = pos;
}

glsl3.frag:
 precision highp float;

#if USE_ALPHA_TEST

  uniform ALPHA_TEST {
    float alphaThreshold;
  }
#endif

void ALPHA_TEST (in vec4 color) {
  #if USE_ALPHA_TEST
      if (color.a < alphaThreshold) discard;
  #endif
}

void ALPHA_TEST (in float alpha) {
  #if USE_ALPHA_TEST
      if (alpha < alphaThreshold) discard;
  #endif
}

 in vec4 v_color;

 #if USE_TEXTURE
 in vec2 v_uv0;
 uniform sampler2D texture;
 #endif

 uniform Mosaic {
   float xBlockCount;
   float yBlockCount;
 }

/**
 * 获取v_uv0最终映射的马赛克格子的坐标
 */
 vec2 getUvMapPos() {

   float xCount;
   if (xBlockCount <= 0.0) {
     xCount = 1.0;
   } else {
     xCount = xBlockCount;
   }
   float blockWidth = 1.0 / xCount;

   float blockXIndex = floor(v_uv0.x / blockWidth);

   float yCount;
   if (yBlockCount <= 0.0) {
     yCount = 1.0;
   } else {
     yCount = yBlockCount;
   }
   float blockHeight = 1.0 / yCount;
   float blockYIndex = floor(v_uv0.y / blockHeight);

   return vec2(blockWidth * (blockXIndex + 0.5), blockHeight * (blockYIndex + 0.5));
 }

 void main () {
   vec4 o = vec4(1.5, 1.5, 1.5, 1);

   vec2 realPos = getUvMapPos();

   #if USE_TEXTURE
   o *= texture(texture, v_uv0);
     #if CC_USE_ALPHA_ATLAS_TEXTURE

     o.a *= texture2D(texture, realPos + vec2(0, 0.5)).r*0.0;

     #endif
   #endif

   o *= v_color;

   ALPHA_TEST(o);

   gl_FragColor = o;
 }

